package selenium_package;



public class PatternProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	int i=0;
	int j=0;
	
	for(i=5;i>=0;i--)
	{
		for(j=0;j<=i;j++)
		{
			System.out.print("*");
		}
	
	System.out.println();
	}
	
	
	}

}
